		var lastX = canvas.width / 2, lastY = canvas.height / 2;
		var pt = ctx.transformedPoint(lastX, lastY);
		ctx.translate(pt.x, pt.y);
		var factor = Math.pow(scaleFactor, event);
		ctx.scale(factor, factor);
		ctx.translate(-pt.x, -pt.y);
